/**--列车列表--*/
IF EXISTS (SELECT * FROM sysobjects WHERE type = 'U' AND name = 'newtrain')
	BEGIN
		DROP  Table newtrain
	END
GO

CREATE TABLE newtrain
(
   
	num           integer,								--序号
  trainname     varchar(50) not null primary key,		--车次
  trainbigkind  integer not null,						--大类
  traintype     varchar(50) not null,					--列车类型
  astation      varchar(50) not null,					--始发站
  bstation      varchar(50) not null,					--终到站
  mile          integer,								--单程距
  lineid        varchar(50),							--线路ID
  kxts          integer,								--开行趟数
  cdzs          numeric(18,2),							--车底组数
  yinzuo        integer,								--编组（硬座）
  ruanzuo       integer,								--编组（软座）
  openyinwo     integer,								--编组（硬卧）
  closeyinwo    integer,								--编组（封闭硬卧）
  ruanwo        integer,								--编组（软卧）
  advanceruanwo integer,								--编组（高级软卧）
  canche        integer,								--编组（餐车）
  fadianche     integer,								--编组（发电车）
  shuyinche     integer,								--编组（行李车）		
  youzhengche   integer,								--编组（邮政车）
  line          varchar(500),							--线路
  fullneirang   nvarchar(1),							--全程内燃机车标志
  checkline     nvarchar(1),							--线路检查标志
  gltrain       varchar(50),							--关联车次
  glyear        integer,								--关联时间（年）
  glmonth       integer,								--关联时间（月）
  direction     varchar(50),							--方向分组
  bdflag        varchar(5),								--（暂不清楚）
  bddate        datetime,								--（暂不清楚）
  direction1    varchar(50)								--（暂不清楚）

)
GO

